-- 用户表
-- -----------------------------
drop table if exists userbase;
create table userbase  (
  userId varchar(50) primary key comment '用户id',
	userNo varchar(50) not null comment '用户名',
  password varchar(50) null comment '密码',
	userName varchar(50) not null comment '姓名',
  userPostbox varchar(50) null comment '邮箱',
  headSculpture varchar(100) null comment '头像',
  source varchar(50) null comment '来源',
	grade int(11) not null comment '等级。0管理员 1普通用户'
) engine = innodb character set = utf8 collate = utf8_general_ci row_format = dynamic;


