-- 课程表
-- ----------------------------
drop table if exists course;
create table course  (
  courseId int(11) primary key comment '课程id',
  courseName varchar(20) not null comment '课程名'
) engine = innodb character set = utf8 collate = utf8_general_ci row_format = dynamic;

