-- 题目表
-- ----------------------------
drop table if exists question;
create table question  (
  questionId int(11) primary key comment '题目id',
  questionText longtext not null comment '题目',
  answer_a longtext not null comment '题目A',
  answer_b longtext not null comment '题目B',
  answer_c longtext not null comment '题目C',
  answer_d longtext not null comment '题目D',
  answer varchar(50) not null comment '正确答案',
  courseId int(11) comment '课程id'
) engine = innodb character set = utf8 collate = utf8_general_ci row_format = dynamic;
-- ALTER TABLE 表名 ADD FOREIGN KEY [外键名字] (外键字段) REFERENCES 父表 (主键字段);
-- 外键关联课程表
alter table question add foreign key FK_course_question (courseId) references course(courseId);


