-- 记录表
-- ----------------------------
drop table if exists statistics;
create table statistics  (
  sId int(11) primary key auto_increment comment '记录ID',
  alltopic text not null comment '所有的题',
  correctanswer text not null comment '正确的题',
  erroranswer text not null comment '错误的题',
  fraction int(11) not null comment '分数',
  userId varchar(50) not null comment '关联用户表',
  starttime varchar(50) not null comment '开始时间',
  endtime varchar(50) not null comment '结束时间',
  answernumber int(11) null comment '答题数量',
  subject varchar(255) not null comment '科目',
  myanswer varchar(225) not null comment '自选答案',
  allanswer text not null comment '答案'
) engine = innodb character set = utf8 collate = utf8_general_ci row_format = dynamic;
-- ALTER TABLE 表名 ADD FOREIGN KEY [外键名字] (外键字段) REFERENCES 父表 (主键字段);
-- 外键关联课程表
alter table statistics add foreign key FK_userbase_statistics (userId) references userbase(userId);
